/*
** Ask a question submit
*/

function ask_a_qn_submit(){
	var question = $("#ask-a-question textarea").val();
	if(question == ''){
		alert('Please submit your question.');
	}
	else{
			$.ajax({
					   type: "GET",
					   url: site_url()+"account/ask-a-qn",
					   data: { question : question},
					   success: function(data)
					   {
							alert(data);
							$("#ask-a-question").modal("hide");
							$("#ask-a-question textarea").val('');
					   }
			});
	}
}

